import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { Button, Modal, Form, FormControl, FormGroup, ControlLabel } from 'react-bootstrap'

import { selectContact } from '../../../actions';
import './list_contacts.css';


class ListContacts extends Component {
    constructor(props) {
        super(props);
        this.forcingUpdate = this.forcingUpdate.bind(this);
        this.renderContactList = this.renderContactList.bind(this);
        this.handleHide = this.handleHide.bind(this)


        this.state = {
            contacts: [],
            show: false
        }
    }

    handleHide() {
        this.setState({ show: false });
    }

    forcingUpdate() {
        this.forceUpdate();
    }

    componentWillReceiveProps(props) {
        // console.log('receiving props... ', props)
        this.forcingUpdate();
        this.render();
    }

    // componentWillMount() {
    //     console.log('Will mount :: ' , this.state.contacts);
    // }

    componentDidMount() {
        this.setState({
            contacts: []
        }, () => {
            this.setState({
                contacts: this.state.contacts.concat(this.props.contacts)
            })
        })
    }

    deleteContact(data) {
        this.props.deleteContact(data);
    }

    editContact(data) {
        console.log('Edit button clicked!'.toUpperCase())
        console.log(data);
        this.setState({
            show: true
        })
        console.log('--------------------------------------');
    }

    editform(){
        return(
            <div>
                <Form inline>
                    <FormGroup controlId="formInlineName" className='mr-2 ml-4'>
                        <ControlLabel>First Name</ControlLabel>{' '}
                        <FormControl onChange={this.handleInputChange} name='firstName' type="text" placeholder="Jane" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineName" className='mr-2'>
                        <ControlLabel>Last Name</ControlLabel>{' '}
                        <FormControl onChange={this.handleInputChange} name='lastName' type="text" placeholder="Doe" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineEmail" className='mr-2'>
                        <ControlLabel>Email</ControlLabel>{' '}
                        <FormControl onChange={this.handleInputChange} name='email' type="email" placeholder="jane.doe@example.com" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineEmail" className='mr-2'>
                        <ControlLabel>Phone Number</ControlLabel>{' '}
                        <FormControl onChange={this.handleInputChange} name='phoneNumber' type="text" placeholder="123-345-5432" />
                    </FormGroup>{' '}
                </Form>

                <br />

                <Form inline>
                    <FormGroup controlId="formInlineName" className='mr-2 ml-4'>
                        <ControlLabel>Address</ControlLabel>{' '}
                        <FormControl onChange={this.handleInputChange} name='addressOne' type="text" placeholder="123 main street" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineName" className='mr-2'>
                        <ControlLabel>Address</ControlLabel>{' '}
                        <FormControl onChange={this.handleInputChange} name='addressTwo' type="text" placeholder="apt 4A" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineEmail" className='mr-2'>
                        <ControlLabel>City</ControlLabel>{' '}
                        <FormControl onChange={this.handleInputChange} name='city' type="text" placeholder="San Francisco" />
                    </FormGroup>{' '}
                </Form>

                <br />

                <Form inline>
                    <FormGroup controlId="formInlineEmail" className='ml-4 mr-2'>
                        <ControlLabel>State</ControlLabel>{' '}
                        <FormControl type="text" onChange={this.handleInputChange} name='state' placeholder="CA" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineEmail" className='mr-2'>
                        <ControlLabel>Zip Code</ControlLabel>{' '}
                        <FormControl type="text" name='zip' onChange={this.handleInputChange} placeholder="94016" />
                    </FormGroup>{' '}
                </Form>

                <div className="form-row text-center">
                    <div className="col-12">
                        <Button
                            bsStyle="primary"
                            bsSize="large"
                            onClick={this.handleSubmit}
                            className='mt-3'
                        >
                            Add Contact
                    </Button>
                    </div>
                </div>
            </div>
        )
    }

    renderContactList() {
        return this.props.contacts.map((element, index) => {
            return (
                <div key={index}>
                    <li className='contacts_list'>
                        <div className="card">
                            <div className="card-header">
                                <h4>{element.firstName} {element.lastName}</h4>
                            </div>
                            <div className="card-body">
                                <p className="card-text">Number: {element.number}</p>
                                <p className="card-text">Email: {element.email}</p>
                                <Button
                                    bsStyle="primary"
                                    onClick={() => {
                                        this.props.selectContact(element)
                                    }}
                                >More Details</Button>
                                <Button
                                    onClick={() => this.deleteContact(element)}
                                    className='ml-1'
                                    bsStyle="danger">Delete</Button>
                                <Button
                                    onClick={() => this.editContact(element)}
                                    className='ml-1'
                                    bsStyle="success">Edit</Button>
                            </div>

                            <div>
                                <div>
                                    {this.state.show ? this.editform() : null}
                                </div>
                            </div>
                        </div>
                    </li>
                    <br />
                </div>
            );
        });
    }

    render() {

        return (
            <div>
                <h3 className="text-center">Contact List</h3>
                <ul>
                    {this.renderContactList()}
                </ul>
            </div>
        )
    }
}


function mapDispatchToProps(dispatch) {
    return bindActionCreators({ selectContact: selectContact }, dispatch)
}

export default connect(null, mapDispatchToProps)(ListContacts);
