import React, {Component} from 'react';
import { connect } from 'react-redux';


class ContactView extends Component{
    render(){
        return(
            <div>
                <h3 className="text-center">View Contact</h3>
            </div>
        )
    }
}

function mapStateToProps(state) {
    const bookDetail = {
        title: state.activeBook,
        pages: null
    }

    state.books.forEach((element, i) => {
        if(element.title === bookDetail.title){
            bookDetail.pages = element.pages
        }
    });

    return {
        // bookDetails: state.activeBook
        contact: {state}
    }
}

export default connect(mapStateToProps)(ContactView);
