import React, { Component } from 'react';
import { Grid, Col, Row } from 'react-bootstrap';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { renderingContacts } from '../../../actions';

import './App.css';
import Header from '../../header/header';
import AddContact from '../add_contact/add_contact';
import ContactView from '../contact_view/contact_view';
import ListContacts from '../list_contacts/list_contacts';

class App extends Component {
  constructor(props) {
    super(props);
    console.log('App Props: ', this.props)
    this.state = {
      status: 0
    }
    setTimeout(() => {
      this.setState({
        status: 1
      })
    }, 3000);
  }

  componentWillMount() {
    console.log('Component Will Mount')
  }

  componentDidMount() {
    console.log('Component Did Mount')
    console.log('----------------------------------');
  }

  componentWillReceiveProps(nextProps) {
    console.log('Component will receive props');
    console.log('nextProps', nextProps);
    console.log('----------------------------------');
  }
  render() {
    return (
      <div>
        <div>
          <Header />
        </div>

        <div>
          <Grid>
            <Row className="show-grid">
              <Col xs={6} md={4}>
                <AddContact />
              </Col>
              <Col xs={6} md={4}>
                <ContactView />
              </Col>
              <Col xsHidden md={4}>
                <ListContacts whenToRender={this.props.renderContacts} />
              </Col>
            </Row>
          </Grid>
        </div>
      </div>
    );
  }
}
function mapStateToProps(state) {
  return {
    renderContacts: state.renderContacts
  }
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators({ renderingContacts: renderingContacts }, dispatch)
}


export default connect(mapStateToProps, mapDispatchToProps)(App);
