import React, { Component } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import {Panel, Heading, Body} from 'react-bootstrap'

import { selectContact } from '../../../actions';


class ListContacts extends Component {
    renderContactList() {
        return this.prop.contacts.map(() => {
            <Panel>
                <Heading>Panel heading without a title</Heading>
                <Body>Panel content</Body>
            </Panel>
        });
    }

    render() {
        return (
            <div>
                <h3 className="text-center">Contact List</h3>
                <ul>
                    {this.renderContactList()}
                </ul>
            </div>
        )
    }
}



function mapStateToProps(state) {
    console.log(state)

    return {
        contacts: state
    }
}

function mapDispatchToProps(dispatch) {
    return bindActionCreators({ selectContact: selectContact }, dispatch)
}

export default connect(mapStateToProps, mapDispatchToProps)(ListContacts);
