import React, { Component } from 'react';
import { Jumbotron, Form, FormControl, FormGroup, ControlLabel, Modal, Button, Col, Grid, Row } from 'react-bootstrap';

import './add_contact.css';


class AddContact extends Component {
    constructor(props, context) {
        super(props, context);

        this.handleHide = this.handleHide.bind(this);

        this.state = {
            show: false
        }
    }


    handleHide() {
        this.setState({ show: false });
    }

    handleInputChange(event) {
        // event.preventDefault();
        const { name, value } = event.target;
        console.log(value)
    }

    handleSubmit() {
        // send state to reducer
    }

    form() {
        return (
            <div>
                <Form inline>
                    <FormGroup controlId="formInlineName" className='mr-2 ml-4'>
                        <ControlLabel>First Name</ControlLabel>{' '}
                        <FormControl type="text" placeholder="first name" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineName" className='mr-2'>
                        <ControlLabel>Last Name</ControlLabel>{' '}
                        <FormControl type="text" placeholder="last name" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineEmail" className='mr-2'>
                        <ControlLabel>Email</ControlLabel>{' '}
                        <FormControl type="email" placeholder="jane.doe@example.com" />
                    </FormGroup>{' '}
                    <FormGroup controlId="formInlineEmail" className='mr-2'>
                        <ControlLabel>Phone Number</ControlLabel>{' '}
                        <FormControl type="text" placeholder="123-345-5432" />
                    </FormGroup>{' '}
                </Form>
                <br/>

                <div className="container-fluid">
                    <div className="row">
                        <div className="col-lg-4">
                            <form>
                                <label>Address
                                <br />
                                    <input
                                        onChange={this.handleInputChange}
                                        type="text"
                                        name='address_one' />
                                </label>

                                <br />
                                <label>Address Two
                                <input
                                        onChange={this.handleInputChange}
                                        type="text"
                                        name='address_two' />
                                </label>
                                <br />
                            </form>

                        </div>
                    </div>
                    <div className="row">
                        <div className="col-lg-4">
                            <form>
                                <label>City
                                <br />
                                    <input
                                        onChange={this.handleInputChange}
                                        type="text"
                                        name='city' />
                                </label>
                                <br />

                                <label>State</label>
                                <br />
                                <input
                                    onChange={this.handleInputChange}
                                    type="text"
                                    name='state' />
                                <br />

                                <label>Zip Code
                                <input
                                        onChange={this.handleInputChange}
                                        type="text"
                                        name='zip_code' />
                                </label>
                                <br />
                            </form>

                        </div>
                    </div>
                </div>


                <div class="form-row text-center">
                    <div class="col-12">
                        <Button
                            bsStyle="primary"
                            bsSize="large"
                            onClick={() => this.handleSubmit()}
                            className='mt-3'
                        >
                            Add Contact
                    </Button>
                    </div>
                </div>




            </div>
        )
    }



    render() {
        return (
            <div>
                <h3 className="text-center">Add Contact</h3>

                <div className="modal-container" style={{ height: 200 }}>
                    <Button
                        bsStyle="primary"
                        bsSize="large"
                        onClick={() => this.setState({ show: true })}
                    >
                        Add Contact
                    </Button>

                    <Modal
                        show={this.state.show}
                        onHide={this.handleHide}
                        bsSize="large"
                        container={this}
                        aria-labelledby="contained-modal-title"
                    >
                        <Modal.Header>
                            <Modal.Title id="contained-modal-title" className='text-center ml-0'>
                                Add Contact
                        </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            {this.form()}
                        </Modal.Body>
                        <Modal.Footer>
                            <Button onClick={this.handleHide}>Close</Button>
                        </Modal.Footer>
                    </Modal>
                </div>
            </div>
        )
    }
}

export default AddContact;
